import React, {useEffect, useState} from "react";
import axios from "axios";
import Spinner from "../ui/Spinner";

const Workers = ({showFlights,setLoading,loading,workers,setWorkers}) => {

        useEffect(() => {
          const fetchWorkers = async () => {
            const result = await axios('https://interview-mock.herokuapp.com/api/workers');
            setWorkers(result.data);
            setLoading(false);
          };  
          fetchWorkers()
        }); // eslint-disable-line 
    return loading ? ( 
        <Spinner />
        ) : (
        <div className="flights">
          {workers.map((p) => (
            <div className="name" key={p.id} onClick={() => showFlights(p.id)}><h4>{p.name}</h4></div>
          ))}  
        </div>
    )
}

export default Workers
