import React, {useEffect, useState} from "react";
import axios from "axios";
import Spinner from "./ui/Spinner";

import Workers from "./components/col1/workers"
import FlightChart from "./components/col2/flightChart"
import FlightInfo from "./components/col3/flight-info"

import {worker,flight} from "./data";

function App() {
  const [loading, setLoading] = useState(false);
  const [workers, setWorkers] = useState([]);
  const [workerId, setWorkerId] = useState(null);
  const [info, setInfo] = useState([]);

  // useEffect(() => {
  //   //fetch data when page loads
  //   const fetchWorkers = async () => {
  //     const result = await axios('https://interview-mock.herokuapp.com/api/workers');
  //     setWorkers(result.data);
  //     console.log(result.data)
  //     setLoading(false);
  //   };  
  //   fetchWorkers()
  // }, []); 

  const showFlights = (e) => {
    setWorkerId(e)
  };
  const showInfo = (e) => {
    setInfo(e)
  };

  return loading ? ( 
    <Spinner />
    ) : (
    <>
      <div className="info-user">
        <h2>Workers</h2>
        <h2>Flights</h2>
        <h2>Flight Date</h2>
      </div>
      <div className="content-wrapper">

        <Workers workers={workers} showFlights={showFlights} setLoading={setLoading}/> 

        <div className="flightWrap">
          {workerId !== null ? <FlightChart showInfo={showInfo} id={workerId}/> : null} 
        </div>

        {info.length !== 0 ? <FlightInfo info={info}  /> : null} 

      </div>
    </>
  );
}

export default App;

