import React, {useEffect, useState} from "react";
import axios from "axios";
import Spinner from "../ui/Spinner";

const FlightChart = ({showInfo, id, setLoading,setFlights,flights,loading}) => {
    //const data = fetchovani podatci
    const data = "podatci od 3 kolone"

        useEffect(() => {
          //fetch data when page loads
          setLoading(true);
          const fetchWorkers = async () => {
            const result = await axios(`https://interview-mock.herokuapp.com/api/workers/${id}`);
            setFlights(result.data);
            setLoading(false);
          };  
          fetchWorkers()
        }, [id]); 

        console.log(flights)
        const info = (i) => {
            return [i.plane,i.duration,i.from_gate,i.to_gate]
        }

    return loading ? ( 
            null
        ) : (

        <div className="flightWrap">
            {flights.map((p) => (
                <div key={p.num} className="flightChart" onClick={(i) => {
                    return showInfo([i.plane,i.duration,i.from_gate,i.to_gate])
                }}>
                    <h4>{p.num}</h4>
                    <h4>{p.from}</h4>
                    <h4>{p.from_date}</h4>
                    <h4>{p.to}</h4>
                    <h4>{p.to.date}</h4>
                </div>
            ))} 
        </div>
