import React, { useState } from "react";

import Workers from "./components/workers"
import FlightChart from "./components/flightChart"
import FlightInfo from "./components/flight-info"

function App() {
  const [loading, setLoading] = useState(true);//set to true
  const [workerId, setWorkerId] = useState(null);
  const [workers, setWorkers] = useState([]);
  const [flights, setFlights] = useState([]);
  const [info, setInfo] = useState([]);

  const showFlights = (e) => {
    setWorkerId(null)
    setWorkerId(e)
    // return flights.length !== 0 ? setFlights([]) : null
  };
  const showInfo = (e) => {
    setInfo(e)
  };
  const handleLoading = (e) => {
    setLoading(e)
  };

  return (
    <>
      <div className="info-user">
        <h2>Workers</h2>
        <h2>Flights</h2>
        <h2>Flight Date</h2>
      </div>
      <div className="content-wrapper">

        <Workers showFlights={showFlights} setLoading={setLoading} loading={loading} workers={workers} setWorkers={setWorkers} setFlights={setFlights}/> 


        {workerId !== null ? <FlightChart setLoading={setLoading} showInfo={showInfo} id={workerId} flights={flights} setFlights={setFlights}/> : null} 


        {info.length !== 0 ? <FlightInfo info={info}  /> : null} 

      </div>
    </>
  );
}

export default App;

